// Led pins
#define LED_RIGHT	5		// GPIO5
#define LED_LEFT 	4		// GPIO4

// Reset pin
#define SAM_RESET	14		// GPIO14

// File names
#define CREDENTIALS_FILE "/nets.txt"
#define MAX_SAVED_CREDENTIALS 8
#define TOKEN_FILE "/token.txt"

// Time servertrue
