class SckESP {
public:
	void setup();
	void update();

	enum Modes { MODE_OTA_UPDATE, MODE_SLEEP, MODE_WIFI, MODE_NO_WIFI };

	// Communication with SAM
	BUS_Serial msgIn;
	BUS_Serial msgOut;
	bool processMsg();
	void SAMsendMsg();
	ESPstatus espStatus;
	void sendStatus();
	void clearParam();
	void SAMlistSavedNetworks();

	// Configuration Management
	String ESPversion = "0.2.0";
	String ESPbuildDate = String(__DATE__) + '-' + String(__TIME__);
	bool saveConf();
	bool loadConf();
	Configuration configuration;

	// WIFI
	void tryConnection();
	void wifiDisconnect();
	bool addNetwork();
	void clearNetworks();
	bool readNetwork(int index=-1);
	void removeNetwork(int index);
	int countSavedNetworks();
	void sendNetwork(uint8_t index, EspCommand comm=ESP_GET_WIFI_COM);
	void clearDuplicated();
	int8_t selectBestNetwork();
	void setGoodNet();
	int actualWIFIStatus;
	int prevWIFIStatus;
	Credentials credentials;
	int scanAP();

	// AP mode
	void startAP();
	void stopAP();
	void startWebServer();
	void stopWebserver();
	void webRoot();
	void webSet();
	void webShow();
	// for captive portal
	const byte DNS_PORT = 53;
	
	// Token
	char token[8];
	void saveToken();
	void readToken();

	// MQTT
	bool mqttStart();
	bool mqttHellow();
	bool mqttPublish();
	bool mqttSend(String payload);
	bool mqttSync();

	// FileSystem
	FSInfo fs_info;
	uint16_t countLines(File myFile);

	// Led control
	void ledSet(uint8_t wichLed, uint8_t value);
	void ledToggle(uint8_t wichLed);
	void ledBlink(uint8_t wichLed, float rate);
	void ledOff();
	uint8_t ledLeft = 0;
	uint8_t ledRight = 1;
	uint8_t ledPin[2] = {LED_LEFT, LED_RIGHT};
	uint8_t ledValue[2] = {0, 0};
	Ticker Lblink;
	Ticker Rblink;

	// Time
	time_t getNtpTime();
	void sendNTPpacket(IPAddress &address);
	String ISOtime();
	String epoch2iso(uint32_t toConvert);
	bool getHttpTime();
	WiFiUDP Udp;
	byte packetBuffer[48];
	
	// Debug
	void debugOUT(String strOut);
	bool serialDebug = false;

	// Sensors
	AllSensors sensors;

private:
};

// Static functions for global calls.
void LedToggleLeft();
void LedToggleRight();

// Utility functions
String leadingZeros(String original, int decimalNumber);

// Time
time_t ntpProvider();

